$(document).ready(function () {
    $(".btn-up").click(function () {
        $("html, body").animate({scrollTop: 0}, 500);
        return false
    });
    $(".strelka .close").click(function () {
        var a = $(this).data("url");
        window.location = window.location.protocol + "//" + window.location.host + a
    });
    $(".ga").click(function () {
        ga("ec:addProduct", {
            id: $(this).data("articul"),
            name: $(this).data("name"),
            category: $(this).data("category"),
            brand: $(this).data("brand")
        });
        ga("ec:setAction", "add");
        ga("send", "event", "UX", "click", "add to cart");
        ga("send", {hitType: "event", eventCategory: "Basket", eventAction: "buy_basket"});
        dataLayer.push({
            ecommerce: {
                add: {
                    products: [{
                        id: $(this).data("articul"),
                        name: $(this).data("name"),
                        price: $(this).data("price"),
                        brand: $(this).data("brand"),
                        category: $(this).data("category"),
                        quantity: 1
                    }]
                }
            }
        })
    });
    $(".ga_detail").click(function () {
        ga("ec:addProduct", {
            id: $(this).data("articul"),
            name: $(this).data("name"),
            category: $(this).data("category"),
            brand: $(this).data("brand")
        });
        ga("ec:setAction", "detail");
        ga("send", "pageview");
        dataLayer.push({
            ecommerce: {
                detail: {
                    products: [{
                        id: $(this).data("articul"),
                        name: $(this).data("name"),
                        price: $(this).data("price"),
                        brand: $(this).data("brand"),
                        category: $(this).data("category")
                    }]
                }
            }
        })
    })
});