$(document).ready(function(){if(!catalogReCalc){$.each(idInBasketBuy,function(a,b){$('[data-productid="'+b+'"]').each(function(c){$(this).find(".buy a").text("В КОРЗИНЕ").addClass("btn-primary").removeClass("btn-default").removeClass("js_add2basket").addClass("js_go2cart")})});$.each(idInBasketFav,function(a,b){$('[data-productid="'+b+'"]').each(function(c){$(this).find(".js_add2wishlist span").addClass("active")})});$.each(idCompare,function(a,b){$('[data-parentproductid="'+b+'"]').each(function(c){$(this).find(".js_add2compare span").removeClass("icon-product-add-comparison").addClass("icon-product-remove-comparison")})});catalogReCalc=true}});